	// and stopping just before the last nth node, i.e., prev node before last nth node.
	while(count!=len-n){
		temp= temp->next;
		count++;
	}

	// now its just simple delete node example
	// we point the next of the prev node to next of last nth node.
	if(temp!= NULL && temp->next!=NULL)
		temp->next= temp->next->next;
	return head;
}
