public static ListNode constructListNode(int[] nodes) {
    ListNode current = new ListNode(nodes[nodes.length - 1]);
    for (int i = nodes.length - 2; i >= 0; i--) {
        current = new ListNode(nodes[i], current);
    }
    return current;
