class Solution {
    public void duplicateZeros(int[] arr) {
        int[] count = new int[arr.length];
        int c=0;

        for( int i =0; i < arr.length; i++ ){
            if( arr[i] == 0 )   
                c++;
            count[i]=c;
        }

        for( int end = arr.length-1; end >= 0; end-- ){
            int newIndex = end + count[end];
            if( newIndex < arr.length )  // new index for the non zero element
                arr[newIndex]=arr[end];
            if( newIndex != end ) //to handle the case if we havent encountered a zero yet
                arr[end]=0;
        }
    }
}
