                target = nums[i] * nums[j]
                total += counts[target]

        return total

    def numTriplets(self, nums1, nums2):

        #Build frequency table of the squares you can form from num1 and num2
        counter1 = collections.Counter([num ** 2 for num in nums1])
        counter2 = collections.Counter([num ** 2 for num in nums2])

        return self.aggregateType(nums1, counter2) + self.aggregateType(nums2, counter1)
