class Solution {
public:
    int twoProduct(long target, vector<int> &nums) {
        unordered_map<int, int> mp;
        int count = 0;

        for(int &x : nums) {
            if(target%x == 0) {
                count += mp[target/x];
            }
            mp[x]++;
        }
        return count;
