            db = Count2.copy()
            while db:
                item = db.popitem()
                Q, r = divmod(i*i, item[0])
                if r == 0 and Q in db:
                    res += db[Q]*item[1]*Count1[i]
                    db.pop(Q)
                elif r == 0 and Q == item[0]:
                    res += comb(item[1],2)*Count1[i]

        for i in Count2:
            db = Count1.copy()
            while db:
                item = db.popitem()
                Q, r = divmod(i*i, item[0])
                if r == 0 and Q in db:
                    res += db[Q]*item[1]*Count2[i]
                    db.pop(Q)
                elif r == 0 and Q == item[0]:
                    res += comb(item[1],2)*Count2[i]

        return res


