        square1 = map(lambda x : x* x, nums1)
        square2 = map(lambda x : x* x, nums2)
        #frequency of nums1^2 and nums2^2
        freq1, freq2 = defaultdict(int),defaultdict(int)
        m1, m2 = range(len(nums1)), range(len(nums2))
        for i in m1:
            freq1[square1[i]] += 1
        for i in m2:
