class Solution:
    def numTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        freq1 = self.get_freq(nums1)
        freq2 = self.get_freq(nums2)
        ans = 0
        ans += self.count(freq1, nums2)
        ans += self.count(freq2, nums1)
        return ans

    def get_freq(self, nums):
        ans = collections.defaultdict(int)
        n = len(nums)
        for i in range(n):
            ans[nums[i] * nums[i]] += 1
        return ans

    def count(self, freq, other):
        ans = 0
        m = len(other)
        for i in range(m):
            for j in range(i+1, m):
                prod = other[i] * other[j]
                if prod in freq:
                    ans += freq[prod]
        return ans
