class Solution:
    def twoProduct(self, num: int, nums: List[int]) -> int:
        hashMap, count = defaultdict(int), 0
        for elem in nums:
            if num % elem == 0:
                count += hashMap[num // elem]
            hashMap[elem] += 1
        return count

    def countForArray(self, nums1: List[int], nums2: List[int]) -> int:
        res, lastRes, lastNum = 0, 0, 0
        nums1.sort()
        for idx in range(len(nums1)):
            if nums1[idx] == lastNum:
                res += lastRes
            else:
                lastRes = self.twoProduct(nums1[idx] * nums1[idx], nums2)
                res += lastRes
            lastNum = nums1[idx]
        return res

    def numTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        return self.countForArray(nums1, nums2) + self.countForArray(nums2, nums1)
