#define ll long long

class Solution {
public:
    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        int ans = 0;
        unordered_map <ll, int> n1_sq, n2_sq, n1_pro, n2_pro;

        //building n1_sq
        for(int i=0; i<nums1.size(); i++)
            n1_sq[((ll)nums1[i]) *((ll)nums1[i])] ++;

        //building n2_sq
        for(int i=0; i<nums2.size(); i++)
            n2_sq[((ll)nums2[i]) *((ll)nums2[i])] ++;

        //bulding n1_pro
        for(int j=0; j<nums1.size(); j++)
            for(int k=j+1; k<nums1.size(); k++) {
                n1_pro[((ll)nums1[j])*((ll)nums1[k])]++;
            }

        //bulding n2_pro
        for(int j=0; j<nums2.size(); j++)
            for(int k=j+1; k<nums2.size(); k++) {
                n2_pro[((ll)nums2[j])*((ll)nums2[k])]++;
            }

        for(auto x : n2_pro)
            if(n1_sq[x.first])
                ans+=(x.second*n1_sq[x.first]);

        for(auto x : n1_pro)
            if(n2_sq[x.first])
                ans+=(x.second*n2_sq[x.first]);

        return ans;
    }
};
