class Solution(object):
    def numTriplets(self, nums1, nums2):

		d1, d2 = Counter(nums1), Counter(nums2)

        ret = 0
        key1 = sorted(d1.keys())
        key2 = sorted(d2.keys())

		# Type 1
        for k1 in key1:
            kk = k1 * k1
            for k2 in key2:
                if k2 > k1: break
                if k2 == k1:
                    if d2[k2] < 2: break
                    ret += d1[k1] * d2[k2] * (d2[k2] - 1)/2
                elif kk % k2 == 0 and d2[kk/k2] > 0:
                    ret += d1[k1] * d2[k2] * d2[kk/k2]

		# Type 2
        for k2 in key2:
            kk = k2 * k2
            for k1 in key1:
                if k1 > k2: break
                if k1 == k2:
                    if d1[k1] < 2: break
                    ret += d2[k2] * d1[k1] * (d1[k1] - 1)/2
                elif kk % k1 == 0 and d1[kk/k1] > 0:
                    ret += d2[k2] * d1[k1] * d1[kk/k1]

        return ret
