class Solution {
    public int numTriplets(int[] nums1, int[] nums2) {
        Arrays.sort(nums1);
        Arrays.sort(nums2);
        return numTriplets1(nums1, nums2) + numTriplets1(nums2, nums1);
    }

    int numTriplets1(int[] nums1, int[] nums2) {
        int cnt = 0;
        for (int n : nums1) {
            long nn = (long) n * n;
            for (int i = 0, j = nums2.length-1; i < j;) {
                long ij = (long) nums2[i] * nums2[j];
                if (ij == nn) {
                    if (nums2[i] == nums2[j]) {
                        cnt += (j - i + 1) * (j - i) / 2;
                        break;
                    } else {
                        int ci = 1;
                        while (nums2[i] == nums2[++i]) {
                            ++ci;
                        }
                        int cj = 1;
                        while (nums2[j] == nums2[--j]) {
                            ++cj;
                        }
                        cnt += ci * cj;
                    }
                } else if (ij < nn) {
                    ++i;
                } else {
                    --j;
                }
            }
        }
        return cnt;
    }
}```

