const getAllPossibleMultipliedPais = nums => {
    const duplets = new Map();
    for (let i = 0; i < nums.length; i++) {
        for (let j = i + 1; j < nums.length; j++) {
            const dupletRes = nums[i] * nums[j];
            duplets.set(dupletRes, (duplets.get(dupletRes) || 0) + 1);
        }
    }

    return duplets;
}

/**
 * @param {number[]} nums
 * @return {Map}
 */
const getAllSquares = nums => {
    const squares = new Map();
    for (let i = 0; i < nums.length; i++) {
        const squareRes = nums[i] * nums[i];
        squares.set(squareRes, (squares.get(squareRes) || 0) + 1);
    }

    return squares;
}

const getMatchesCount = (dupletsMap, squaresMap) => {
    let matches = 0;
    for (let [duplet, freq] of dupletsMap) {
        const matchedSquareFreq = squaresMap.get(duplet);
        if (matchedSquareFreq === undefined) {
            continue;
        }

        matches += freq * matchedSquareFreq;
    }

    return matches;
}

