func gen(nums []int) map[int]int {
	m := make(map[int]int)
	for i := 0; i < len(nums)-1; i++ {
		for y := i + 1; y < len(nums); y++ {
			m[nums[i]*nums[y]]++
		}
	}
	return m
}

func find(m map[int]int, nums int) int {
	return m[nums]
}

func numTriplets(nums1 []int, nums2 []int) int {
	m1, m2 := gen(nums1), gen(nums2)

	var c int
	for i := 0; i < len(nums1); i++ {
		c += find(m2, nums1[i]*nums1[i])
	}

	for i := 0; i < len(nums2); i++ {
		c += find(m1, nums2[i]*nums2[i])
	}
	return c
}
