class Solution {
public:
    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
      int ans = 0;
      unordered_map<long,int> st1;                   //for all products for arr1
      unordered_map<long,int> st2;                   //for all products for arr2

      for(int i = 0; i < nums1.size(); i++)              //contain all products from arr1 to hash
        for(int j = i + 1; j < nums1.size(); j++){
          long t = long(nums1[i])*nums1[j];
          if(st1.count(t)) st1[t]++;
          else st1[t] = 1;
        }

      for(int i = 0; i < nums2.size(); i++)                  //contain all products from arr2 to hash
        for(int j = i + 1; j < nums2.size(); j++){
          long t = long(nums2[i])*nums2[j];
          if(st2.count(t)) st2[t]++;
          else st2[t] = 1;
        }

      for(int i = 0; i < nums1.size(); i++){             //and check
        long t = long(nums1[i])*nums1[i];
        if(st2.count(t)) ans += st2[t];
      }

      for(int i = 0; i < nums2.size(); i++){
        long t = long(nums2[i])*nums2[i];
        if(st1.count(t)) ans += st1[t];
      }

      return ans;
    }
};
