class Solution {
public:
    int func(vector<int>& nums1, vector<int>& nums2){
        int ret = 0;
        for(int i=0; i<nums1.size(); i++){
            int left = 0;
            int right = nums2.size()-1;
            long cur1 = (long)nums1[i] * (long)nums1[i];
            while(left <= right){
                long cur2 = (long)nums2[left] * (long)nums2[right];
                if(cur2 < cur1){
                    left++;
                }
                else if(cur2 > cur1){
                    right--;
                }
                else{
                    int lcnt = 1;
                    int rcnt = 1;
                    while(left+1 < nums2.size() && nums2[left] == nums2[left+1]){
                        lcnt++;
                        left++;
                    }
                    while(right-1 >= 0 && nums2[right] == nums2[right-1]){
                        rcnt++;
                        right--;
                    }

                    if(nums2[left] != nums2[right]) ret += lcnt*rcnt;
                    else ret += lcnt*(rcnt-1)/2;
                    left++;
                    right--;
                }
            }
        }
        return ret;
    }
    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        sort(nums1.begin(), nums1.end());
        sort(nums2.begin(), nums2.end());
        return func(nums1,nums2)+func(nums2,nums1);

    }
};
