**Description**

Find the number of islands in `grid2`, for which there is an island in `grid1` that contains all the cells that make up this island in `grid2`.

**Program**

```cpp []
class Solution {
private:
    void dfs(int r, int c, vector<vector<int>>& grid1, vector<vector<int>>& grid2, bool& check) {
        int m = grid2.size(), n = grid2[0].size();
        if (r < 0 || r >= m || c < 0 || c >= n || grid2[r][c] == 0) {
            return;
        }
        if (grid1[r][c] == 0 && grid2[r][c] == 1) {
            check = false;
        }
        grid2[r][c] = 0;
        dfs(r - 1, c, grid1, grid2, check);
        dfs(r + 1, c, grid1, grid2, check);
        dfs(r, c - 1, grid1, grid2, check);
        dfs(r, c + 1, grid1, grid2, check);
    }
public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int m = grid2.size(), n = grid2[0].size();
        int result = 0;
        for (int r = 0; r < m; r++) {
            for (int c = 0; c < n; c++) {
                if (grid2[r][c] == 1) {
                    bool check = true;
                    dfs(r, c, grid1, grid2, check);
                    result += check;
                }
            }
        }
        return result;
    }
};
```

