class Solution {
public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int m = grid1.size();
        int n = grid1[0].size();
        p = vector<int>(m * n + 1);
        cnt = m * n;
        rank = vector<int>(m * n + 1, 1);
        iota(p.begin(), p.end(), 0);
        vector<pair<int,int>> dir = {{0, 1}, {1, 0}};
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid2[i][j] == 0) {
                    cnt--;
                    continue;
                } 
                int a = i * n + j;
                for (int k = 0; k < dir.size(); k++) {
                    int x = i + dir[k].first, y = j + dir[k].second;
                    if (x >= m || y >= n) continue;
                    if (grid2[x][y] == 0) continue;
                    int b = x * n + y;
                    comb(a, b);
                }
            }
        }
        unordered_set<int> invalide;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid2[i][j] == 0) continue;
                int root = find(i * n + j);
                if (grid1[i][j] == 0) {
                    invalide.insert(root);
                }
            }
        }
        return cnt - invalide.size();
    }
private:
    vector<int> p;
    vector<int> rank;
    int cnt = 0;;
    void comb(int a, int b) {
        int roota = find(a);
        int rootb = find(b);
        if (roota == rootb) return;
        cnt--;
        if (rank[roota] > rank[rootb]) {
            p[rootb] = roota;
            rank[roota] += rank[rootb];
        } else {
            p[roota] = rootb;
            rank[rootb] += rank[roota];
        }
        return;
    }
    int find(int a) {
        if (p[a] != a) {
            p[a] = find(p[a]);
        }
        return p[a];
    }

};
