        if(grid2[i][j] == 1 and grid1[i][j] == 1){
            grid2[i][j]=2;
        }else{
            return;
        }
        dfs(grid1,grid2,i+1,j);
        dfs(grid1,grid2,i,j+1);
        dfs(grid1,grid2,i-1,j);
        dfs(grid1,grid2,i,j-1);
