class Solution {
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int count = 0;
        for(int i = 0; i < grid2.length; i++){
            for(int j = 0; j < grid2[i].length; j++){
                if(grid2[i][j] == 1){
                    if(solve(grid1, grid2, i, j))
                    count++;
                }
            }
        }
        return count;

    }

    public boolean solve(int[][] grid1, int[][] grid2, int r, int c){
        if(r < 0 || r >= grid2.length ||c < 0 || c >= grid2[r].length || grid2[r][c] == 0) return true;
        boolean flag = grid1[r][c] == 1;
        grid2[r][c] = 0;
        boolean right = solve(grid1, grid2, r+1, c);
        boolean left = solve(grid1, grid2, r-1, c);
        boolean up = solve(grid1, grid2, r, c+1);
        boolean down = solve(grid1, grid2, r, c-1);
        return flag && up && left && right && down;
    }
}
