        for(int i=0;i<n;++i){
            for(int j=0;j<m;++j){
                if(grid2[i][j]){
                    int res=true;
                    DFS(grid1,grid2,i,j,n,m,res);
                    ans += res;   
                }
            }
        }
        return ans;
    }
};
