class Solution {
    private final int water = 0;
    private final int land = 1;
    private int rows;
    private int cols;
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        rows = grid1.length;
        cols = grid1[0].length;
        int count = 0;
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (grid2[i][j] == land && grid1[i][j] == land) {
                    count += countSubIslands(grid1, grid2, i, j);
                }
            }
        }
        return count;
    }
    public int countSubIslands(int[][] grid1, int[][] grid2, int i, int j) {
        if (i < 0 || j < 0 || i >= rows || j >= cols || grid2[i][j] == water) {
            return 1;
        }
        int current = 1;
        if (grid1[i][j] == water) {
            current = 0;
        }
        grid2[i][j] = water;
        return current 
            * countSubIslands(grid1, grid2, i - 1, j)
            * countSubIslands(grid1, grid2, i + 1, j)
            * countSubIslands(grid1, grid2, i, j - 1)
            * countSubIslands(grid1, grid2, i, j + 1);
    }
}
