class Solution {
public:
int n, m, fl, vis[505][505];
    vector<vector<int>> grids1, grids2;

    bool valid(int x, int y) {
        if(x >= 0 and x <n and y >= 0 and y < m and !vis[x][y] and grids2[x][y]) return true;
        else return false;
    }

    void dfs(int x, int y) {
        if(valid(x, y)) {
            if(!grids1[x][y]) {
                fl = 0;
            }
            vis[x][y] = 1;
            dfs(x+1, y);
            dfs(x-1, y);
            dfs(x, y+1);
            dfs(x, y-1);
        }
    }

    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        memset(vis, 0, sizeof(vis));
        grids1 = grid1, grids2 = grid2;
        n = grid1.size(), m = grid1[0].size();

        int ans = 0;
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(valid(i, j)) {
                    fl = 1;
                    dfs(i, j);
                    if(fl) ans++;
                }
            }
        }
        return ans;
    }
};
