        if (i<0 || j<0 || i >= m || j>=n) return;
        if (grid[i][j] == 0) return;

        grid[i][j] = 0;
        dfs(grid, i+1, j);
        dfs(grid, i-1, j);
        dfs(grid, i, j+1);
        dfs(grid, i, j-1);
    }
