Time: `O(MN)`
Space: `O(MN)`
```
import collections
class Solution:
    def countSubIslands(self, grid1: List[List[int]], grid2: List[List[int]]) -> int:
        def find(pos: tuple) -> tuple:
            if parent[pos] != pos:
                parent[pos] = find(parent[pos])
            return parent[pos]

        def union(x: tuple, y: tuple):
            px, py = map(find, (x, y))
            if px != py:
                if rank[px] > rank[py]: px, py = py, px
                parent[px] = py
                rank[py] += int(rank[px] == rank[py])

        def sink(x: int, y: int) -> bool:
            queue = collections.deque([(x, y)])
            grid2[x][y] = 0
            ans = True
            while queue:
                i, j = queue.popleft()
                for r, c in (i+1, j), (i-1, j), (i, j+1), (i, j-1):
                    if 0 <= r < m and 0 <= c < n and grid2[r][c] == 1:
                        ans &= find((r, c)) == find((x, y))
                        grid2[r][c] = 0
                        queue.append((r, c))
            return ans

        m, n = len(grid1), len(grid1[0])
        parent = {(i, j): (i, j) for i in range(m) for j in range(n)}
        rank = collections.Counter()
        for i in range(m):
            for j in range(n):
                if grid1[i][j] == 1:
                    if i > 0 and grid1[i-1][j] == 1:
                        union((i, j), (i-1, j))
                    if j > 0 and grid1[i][j-1] == 1:
                        union((i, j), (i, j-1))

        return sum(sink(i, j) for i in range(m) for j in range(n)
                   if grid1[i][j] == 1 and grid2[i][j] == 1)
