bool bfs(int x,int y,vector<vector<int>>& grid2,
                         vector<vector<int>>& grid1){
        int m = grid2.size();
        int n = grid2[0].size();
        queue<pair<int,int>> q;
        q.push({x,y});
        int dx[]= {-1,0,1,0};
        int dy[]= {0,1,0,-1};
        bool flag = true;
        while(!q.empty()){
            int r = q.front().first;
            int c = q.front().second;
            q.pop();
            for(int i = 0;i<4;i++){
                int nr = r+dx[i];
                int nc = c+dy[i];
                if(nr>=0 && nc>=0&& nr<m && nc < n && grid2[nr][nc]==1){
                    if(grid1[nr][nc]==0)
                        flag = false;
                    else {
                        grid1[nr][nc] = 0;
                        grid2[nr][nc] = 0;
                        q.push({nr,nc});
                    }
                }

            }

        }
        return flag;
    }
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int count = 0;
        for(int i = 0;i<grid2.size();i++){
            for(int j = 0;j<grid2[i].size();j++){
                if(grid2[i][j] && grid1[i][j]){
                    count += bfs(i,j,grid2,grid1);
                }
            }
        }
        return count;
    }
