        int dxdy[][4] = {{-1,0}, {0,1}, {1,0}, {0,-1}};

        int count = 0;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(grid2[i][j] == 1){
                    bool flag = true;
                    if(dfs(grid1, grid2, i, j, flag)){
                        count++;
                    }
                }
            }
        }
        return count;
