    bool dfs(vector<vector<int>>& grid1, vector<vector<int>>& grid2, int row, int col, bool& flag){
        int n = grid1.size();
        int m = grid1[0].size();

        if(row<0 || col<0 || row>=n || col>=m || grid2[row][col] == 0){
            return true;
        }

        if(grid1[row][col] == 0){
            flag = false;
        }

        grid2[row][col] = 0;

        dfs(grid1, grid2, row-1, col, flag);
        dfs(grid1, grid2, row, col+1, flag);
        dfs(grid1, grid2, row+1, col, flag);
        dfs(grid1, grid2, row, col-1, flag);

        return flag;  
    }
public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int n = grid1.size();
        int m = grid1[0].size();
        int dxdy[][4] = {{-1,0}, {0,1}, {1,0}, {0,-1}};

        int count = 0;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(grid2[i][j] == 1){
                    bool flag = true;
                    if(dfs(grid1, grid2, i, j, flag)){
                        count++;
                    }
                }
            }
        }
        return count;
    }
};

