class Solution {
public:

    map<pair<int,int>,bool>vis;
    int k=0;
    bool check(vector<vector<int>>&g2,int x,int y){
        if(x>=g2.size() or x<0 or y>=g2[0].size() or y<0 or g2[x][y]==0)return false;
        return true;
    }

    void dfs(vector<vector<int>>&g1,vector<vector<int>>&g2,int x,int y){
        vis[{x,y}]=true;
        if(g1[x][y]!=1)k=1;
        if(check(g2,x+1,y) and vis[{x+1,y}]==false)dfs(g1,g2,x+1,y);
        if(check(g2,x-1,y) and vis[{x-1,y}]==false)dfs(g1,g2,x-1,y);
        if(check(g2,x,y+1) and vis[{x,y+1}]==false)dfs(g1,g2,x,y+1);
        if(check(g2,x,y-1) and vis[{x,y-1}]==false)dfs(g1,g2,x,y-1);

    }

    int countSubIslands(vector<vector<int>>&g1, vector<vector<int>>&g2) {
        int n=g1.size();
        int m=g1[0].size();
        int cnt=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[{i,j}] or g2[i][j]==0)continue;
                dfs(g1,g2,i,j);
                if(k==0)cnt++;
                k=0;
            }
        }return cnt;
    }
};

