void dfs(vector<vector<int>>& g1, vector<vector<int>>& g2,int i,int j,int n,int m,int &f)
    {
        //if put of bounds or we have a 0 cell then return
        if(i<0||j<0||i>=n||j>=m||g2[i][j]==0)
            return;
        //if we have a 0 in g1 at (i,j) cell then that means we do not have a matching island hence change flag f to 0 and return
        if(g1[i][j]==0)
        {
            f=0;
            return;
        }
        //if not the above conditions then mark the current g2 cell visited by making it 0 and make the adjacent cells visited by making recursive calls on all 4 directions
        g2[i][j]=0;
        dfs(g1,g2,i+1,j,n,m,f);
        dfs(g1,g2,i-1,j,n,m,f);
        dfs(g1,g2,i,j+1,n,m,f);
        dfs(g1,g2,i,j-1,n,m,f);
    }

    int countSubIslands(vector<vector<int>>& g1, vector<vector<int>>& g2) {
        int n=g1.size(),m=g1[0].size(),ans=0;
        //apply dfs of marking island on every cell having 1 in g2
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                //if we have a 1 cell
                if(g2[i][j]==1)
                {
                    //initially we consider that we have a matching island in g1 by keeping f=1
                    int f=1;
                    //we will change f if we do not find a match in g1
                    dfs(g1,g2,i,j,n,m,f);
                    //add the altered f
                    ans+=f;
                }
            }
        }
        return ans;
    }

