class Solution {
     public void DFS(int[][]grid, int r, int c, int m, int n){

        if(r<0 || c<0 ||  r>=m || c>=n ||  grid[r][c]!=1 )return;

        grid[r][c]=2;
        DFS(grid,r+1,c,m,n);
         DFS(grid,r,c+1,m,n);
        DFS(grid,r-1,c,m,n);

        DFS(grid,r,c-1,m,n);

    }

    public int countSubIslands(int[][] grid1, int[][] grid2) {

	/*
	To be a sub island, grid2,grid1 both should be land, if it happen like grid2  is land and grid1 is water
	then there can't be a sub islands.


	*/
