        int n = grid2.length, m = grid2[0].length;
       for(int i =0;i<n;i++){
           for(int j =0;j<m;j++){
               if(grid2[i][j]==1 && grid1[i][j]==0){
                   DFS(grid2,i,j,n,m);
               }
           }
       }

	   //after marking grid, we have to count number of island.... and that will be our answer.

        int count =0;
        for(int i =0;i<n;i++){
