class Solution {
public:
    bool dfs(vector<vector<int>>& sub, vector<vector<int>>& super,int i,int j,int m,int n)
    {
        if(i<0 || j<0 || i>=m || j>=n || sub[i][j]==0)
            return true;
        bool ans=super[i][j]==sub[i][j];
        sub[i][j]=0;
        ans = dfs(sub, super, i+1, j, m, n) && ans;
        ans = dfs(sub, super, i, j+1, m, n) && ans;
        ans = dfs(sub, super, i-1, j, m, n) && ans;
        ans = dfs(sub, super, i, j-1, m, n) && ans;
        return ans;

    }
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int m = grid1.size();
        int n = grid1[0].size();
        int count =0;

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
