class Solution {
    public class pair{
        int row;
        int col;

        pair(int row,int col){
            this.row = row;
            this.col = col;
        }
    }

    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int [][] dir = {{0,1},{1,0},{-1,0},{0,-1}};

        int n = grid1.length,m = grid1[0].length;
        int count = 0;
        for(int i = 0;i < n;i++){
            for(int j = 0;j < m;j++){

                if(grid2[i][j] == 0)
                    continue;

                LinkedList<pair> que = new LinkedList<>();
                que.addLast(new pair(i,j));
                boolean flag = true;
                while(que.size() != 0){
                    pair rm = que.removeFirst();

                    int row = rm.row;
                    int col = rm.col;

                    if(grid1[row][col] == 0)
                        flag = false;

                    for(int[] d : dir){
                        int r = row + d[0];
                        int c = col + d[1];

                        if(r >= 0 && c >= 0 && r < n && c < m && grid2[r][c] == 1){
                            grid2[r][c] = 0;
                            que.addLast(new pair(r,c));
                        }
                    }
                }

                if(flag)
                    count++;
            }
        }

        return count;
    }
}
