```
class Solution {
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int count = 0;

        for (int i = 0; i < grid1.length; i++) {
            for (int j = 0; j < grid2[0].length; j++) {

                if (grid1[i][j] == 1 && grid2[i][j] == 1)
                {
                    if (countIsland(grid1, grid2, i, j)){
                        count++;
                    }
                }
            }
        }
        return count;

    }

    private boolean countIsland(int[][] grid1, int[][] grid2, int row, int col) {

        if (row < 0 || col < 0 || row >= grid2.length || col >= grid2[0].length
           || grid2[row][col] != 1) return true; // out of boundary and visited will be treated as positive cases

        if (grid2[row][col] == 1 && grid1[row][col] != 1) return false; //false only when both places dont have 1

        grid2[row][col] = -1; //making visited
        boolean left = countIsland(grid1, grid2, row -1, col);
        boolean right = countIsland(grid1, grid2, row +1, col);
        boolean down = countIsland(grid1, grid2, row, col+1);
        boolean up = countIsland(grid1, grid2, row, col-1);

        if (left && right && up && down) return true;

        return false;

    }
}
