```
class Solution {
private:
    void dfs(vector<vector<int>>& grid, int i, int j, int rows, int cols, vector<vector<bool>> &visited, bool &flag) {
        // checking bounds
        if(i < 0 || i >= rows || j < 0 || j >= cols || grid[i][j] == 0)
            return ;

        // if any cell of this region/island is outside of first grid
        if(visited[i][j] == false) { // visited[i][j] == false means the area does not lie in first grid
            flag = false;
            return ;
        }

        grid[i][j] = 0; // marking visited

        // checking for neighbour cells
        dfs(grid, i + 1, j, rows, cols, visited, flag);
        dfs(grid, i - 1, j, rows, cols, visited, flag);
        dfs(grid, i, j + 1, rows, cols, visited, flag);
        dfs(grid, i, j - 1, rows, cols, visited, flag);
    }

public:
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int rows = grid1.size();
        int cols = grid1[0].size();

        vector<vector<bool>> visited(rows, vector<bool>(cols, false));

        // filling visited from grid1
        for(int i = 0; i < rows; ++i) {
            for(int j = 0; j < cols; ++j) {
                if(grid1[i][j] == 1) {
                    visited[i][j] = true;
                }
            }
        }

        int count = 0;

        // for grid2
        for(int i = 0; i < rows; ++i) {
            for(int j = 0; j < cols; ++j) {
                if(grid2[i][j] == 1) {
                    bool flag = true;

                    dfs(grid2, i, j, rows, cols, visited, flag);

                    if(flag) // means after dfs check, it's region is inside grid1's
                        ++count;
                }
            }
        }
        return count;
    }
};
```
