class Solution {
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int n = grid1.length;
        int m = grid1[0].length;
        boolean[][] visited = new boolean[n][m];
        int ans = 0;
        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++) {
                if(!visited[i][j] && grid1[i][j] == 1 && grid2[i][j] == 1 && traverseIsland(grid1,grid2,i,j,visited)) {
                    ans++;
                }
            }
        }
        return ans;
    }


    boolean traverseIsland(int[][] grid1, int[][] grid2, int row, int col, boolean[][] visited) {
        if(row<0 || col<0 || row>=grid1.length || col>= grid1[0].length || visited[row][col] || grid2[row][col]!=1) return true;
        if(grid1[row][col]!=1)
            return false;
        visited[row][col] = true;
        boolean ans = true;
        ans = ans & traverseIsland(grid1,grid2,row-1,col,visited);
        ans = ans & traverseIsland(grid1,grid2,row+1,col,visited);
		ans = ans & traverseIsland(grid1,grid2,row,col-1,visited);
        ans = ans & traverseIsland(grid1,grid2,row,col+1,visited);
        return ans;
    }
}
