public class Solution {
    public int CountSubIslands(int[][] grid1, int[][] grid2) {

        int m = grid1.Length, n = grid1[0].Length, ans = 0;
		//Remove Uncommon Islands
        for(int i = 0 ; i < m ; i++)
        {
            for(int j = 0 ; j < n ; j++)
            {
                if(grid1[i][j] == 0 && grid2[i][j] == 1)
                {
                    DFS(grid2, i, j);
                }
            }
        }
        //Count Final number of islands.
        for(int i = 0 ; i < m ; i++)
        {
            for(int j = 0 ; j < n ; j++)
            {
                if(grid2[i][j] == 1)
                {
                    ans++;
                    DFS(grid2, i, j);
                }
            }
        }

        return ans;

    }

    public void DFS(int[][] grid, int i, int j)
    {
        if(i < 0 || j < 0 || i >= grid.Length || j >= grid[0].Length || grid[i][j] != 1)
            return;
        grid[i][j] = 0;
        DFS(grid, i-1, j);
        DFS(grid, i+1, j);
        DFS(grid, i, j-1);
        DFS(grid, i, j+1);
    }
}
