class Solution {
private:
    void dfs(vector<vector<int>>& grid1, vector<vector<int>>& grid2, int i, int j, bool &flag) {
        if(i>=0 && j>=0 && i<grid2.size() && j<grid2[0].size() && grid2[i][j] == 1){
            grid2[i][j] = 0;
            if(grid1[i][j] == 0){
                flag = false;
            }
            dfs(grid1,grid2,i+1,j,flag);
            dfs(grid1,grid2,i-1,j,flag);
            dfs(grid1,grid2,i,j-1,flag);
            dfs(grid1,grid2,i,j+1,flag);
        }
