		if (row && grid[row - 1][col] == 1) turnToWater(grid, row - 1, col);
		if (col && grid[row][col - 1] == 1) turnToWater(grid, row, col - 1);
		if (row < maxRows - 1 && grid[row + 1][col] == 1) turnToWater(grid, row + 1, col);
		if (col < maxCols - 1 && grid[row][col + 1] == 1) turnToWater(grid, row, col + 1);
	}
};
