class Solution:
    def countSubIslands(self, grid1: List[List[int]], grid2: List[List[int]]) -> int:

        m = len(grid1) # No. of rows of a grid, say grid1
        n = len(grid1[0]) # Size of list at position 0 of grid1

        def dfs(grid, i, j):
            if i<0 or j<0 or i>=m or j>=n: # If we are outside grid, return False
                return False

            if grid[i][j] == 0: # If element of grid is water, return True
                return True

            grid[i][j] = 0 # Mark traversed element as water in grid

            # Do this same recursively for it's surroundings
            dfs(grid, i+1,j)
            dfs(grid, i-1,j)
            dfs(grid, i,j+1)
            dfs(grid, i,j-1)


        # We remove the cases where grid1 has water and grid2 has land
        for i in range(m):
            for j in range(n):
                if grid1[i][j] == 0 and grid2[i][j] == 1:
                    dfs(grid2, i, j)

        # Here once we get the final grid2 matrix, we check how many sub-islands are present in grid2
        count = 0
        for i in range(m):
            for j in range(n):
                if grid2[i][j] == 1:
                    count+=1
                    dfs(grid2, i, j)
        return count # Returns the count of sub-islands
