class Solution {
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        int row = grid2.length;
        int col = grid2[0].length;
        boolean[][] visit = new boolean[row][col];
        int subIsland = 0;
        for (int i = 0; i < row; i++) {
            for (int j = 0; j < col; j++) {
                if (grid2[i][j] == 1 && !visit[i][j] && dfs(grid1, grid2, visit, i, j)) {
                    subIsland += 1;
                }
            }
        }

        return subIsland;
    }

    private static boolean dfs(int[][] grid1, int[][] grid2, boolean[][] visit, int i, int j) {
        if (i < 0 || i >= grid2.length || j < 0 || j >= grid2[i].length || visit[i][j] || grid2[i][j] == 0) {
            return true;
        }
        visit[i][j] = true;
        boolean isMatched = true;
        if (grid1[i][j] == 0) {
            isMatched = false;
        }

        isMatched = dfs(grid1, grid2, visit, i+1, j) && isMatched;
        isMatched = dfs(grid1, grid2, visit, i-1, j) && isMatched;
        isMatched = dfs(grid1, grid2, visit, i, j+1) && isMatched;
        isMatched = dfs(grid1, grid2, visit, i, j-1) && isMatched;

        return isMatched;
    }
}
