```
class Solution {
public:
    int dx[4]={0,1,0,-1};
    int dy[4]={1,0,-1,0};
    int vis[501][501];
    bool isValid(vector<vector<int>>& grid1, vector<vector<int>>& grid2, int x,int y){
        if(x<0||x>=grid2.size()||y<0||y>=grid2[0].size()||grid2[x][y]==0||grid2[x][y]!=grid1[x][y]||vis[x][y]==1)
            return false;
        return true;
    }
    void dfs(vector<vector<int>>& grid1, vector<vector<int>>& grid2, int x,int y){

        vis[x][y]=1;
        for(int i=0;i<4;i++){
            if(isValid(grid1,grid2,x+dx[i],y+dy[i]))
            dfs(grid1,grid2,x+dx[i],y+dy[i]);
        }

    }
    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int cnt=0;
        //Traverse through the first grid and check if for any water there is land in grid 2
        // if true then make that land and its connected component as visited
        for(int i=0;i<grid2.size();i++)
            for(int j=0;j<grid2[0].size();j++){
                if(grid1[i][j]==0 && grid2[i][j]==1)
                    dfs(grid1,grid2,i,j);
            }
        //Traverse through the seconnd grid and count the no of connected components
        for(int i=0;i<grid2.size();i++)
            for(int j=0;j<grid2[0].size();j++)
                if(grid2[i][j]==1 && vis[i][j]==0){
                    cnt++;
                    dfs(grid1,grid2,i,j);

                }
        return cnt;
    }
};
