class Solution {
    boolean ans;
    public int countSubIslands(int[][] grid1, int[][] grid2) {
        //none of element must be outside of grid1's land in grid2's land
        int n = grid1.length;
        int m = grid1[0].length;
        int count = 0;
        for(int i = 0; i<n; i++)
        {
            for(int j = 0; j<m; j++)
            {
                if(grid1[i][j] == 1 && grid2[i][j] == 1) //only possible sub island can be found
                {
                    ans = true;
                    dfs(grid1, grid2, i, j);
                    if(ans)
                    {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    //grid1[i][j] == 0, grid[i][j] == 1 is not acceptable
    //0, 0
    //1, 1
    //1, 0 these are acceptable in case of sub island
    int[][] dirs = {{1,0},{0,1},{-1,0},{0,-1}};

    public void dfs(int[][] grid1, int[][] grid2, int i, int j)
    {
        if(grid1[i][j] == 0) ans = false; //simply if grid2[i][j] is 1 but grid1[i][j] = 0 (only false case)
        grid2[i][j] = 0;

        for(int[] dir : dirs)
        {
            int x = i+dir[0];
            int y = j+dir[1];
            if(x>=0 && y>=0 && x<grid2.length && y<grid2[0].length && grid2[x][y] == 1)
            {
                dfs(grid1, grid2, x, y);
            }
        }

    }

}
