class Solution {
public:
    void DFS(int i, int j, int m, int n, vector<vector<int>>& grid1, vector<vector<int>>& grid2, bool& res) {

        // Corner cases
        if(i<0 || i>=m || j<0 || j>=n || grid2[i][j] == 0) return;

        // If grid1 does not have land for the corresponding cell in grid2
        if(grid1[i][j] == 0) {
            res = false;
            return;
        }

        // Marking visited
        grid2[i][j] = 0;

        // DFS in four directions
        DFS(i+1, j, m, n, grid1, grid2, res);
        DFS(i-1, j, m, n, grid1, grid2, res);
        DFS(i, j+1, m, n, grid1, grid2, res);
        DFS(i, j-1, m, n, grid1, grid2, res);

    }

    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int m = grid2.size();
        int n = grid2[0].size();

        int ans=0;

        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                if(grid2[i][j] == 1) {
                    bool res = true;
                    DFS(i, j, m, n, grid1, grid2, res);

                    // If grid1 has land for every corresponding land (single component) in grid2
                    if (res) ans++;
                }
            }
        }

        return ans;
    }
};
