class Solution {
public:
    bool check = true; // to check whether grid1 is contained grid2

    void dfs(vector<vector<int>>& grid2, vector<vector<int>>& grid1, int row, int col){
        // bundary checking
        if(row<0 || col<0 || row>=grid2.size() || col >=grid2[0].size() || grid2[row][col] == 0) return;

        grid2[row][col] = 0;
        // if do not equal to 1, it means grid1 does not contain this point of grid2.
        if(grid1[row][col] != 1){
             check = false;
        }
        // go to up/down/left/right, this four directions
        dfs(grid2, grid1, row+1, col);
        dfs(grid2, grid1, row-1, col);
        dfs(grid2, grid1, row, col+1);
        dfs(grid2, grid1, row, col-1);
    }

    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int R = grid1.size();
        int C = grid1[0].size();
        int ans = 0;
        for(int row = 0; row<R; row++){
            for(int col=0; col<C; col++){
                if(grid2[row][col] == 1){
                    dfs(grid2, grid1, row, col); 

                    // if check is true, it means grid1 contained grid2
                    if(check) ans++;
                    check = true;
                }
            }
        }
        return ans;
    }
};
