class Solution:
    def countSubIslands(self, grid1: List[List[int]], grid2: List[List[int]]) -> int:
        m, n = len(grid2), len(grid2[0])

        def dfs(i, j):
            if i < 0 or j < 0 or i == m or j == n or grid2[i][j] == 0:
                return True

            is_subisland = True
            if grid1[i][j] == 0:
                is_subisland = False

            grid2[i][j] = 0

            is_subisland = dfs(i+1, j) and is_subisland
            is_subisland = dfs(i-1, j) and is_subisland
            is_subisland = dfs(i, j+1) and is_subisland
            is_subisland = dfs(i, j-1) and is_subisland

            return is_subisland

        sub_islands = sum(grid2[i][j] and dfs(i, j) for i in range(m) for j in range(n))
        return sub_islands
