class Solution {
    static int f=1;
    public int countSubIslands(int[][] g1, int[][] g2) {
        int c=0;
        for(int i=0;i<g2.length;i++)
        {
            for(int j=0;j<g2[0].length;j++)
            {
                if(g2[i][j]==1)
                {
                    f=1;
                    task(g1,g2,i,j);
                    c+=f;
                }
            }
        }
        return c;
    }
    static void task(int[][] g1, int[][] g2, int i, int j)
    {
        if(i<0||j<0||i>=g2.length||j>=g2[0].length||g2[i][j]==0)
            return;
        if(g1[i][j]==0)
            f=0;
        g2[i][j]=0;
        task(g1,g2,i+1,j);
