class Solution {
public:
    bool isSubIsland(vector<vector<int>>& grid1, vector<vector<int>>& grid2, int i, int j) {
        int m = grid1.size();
        int n = grid1[0].size();

        if (i < 0 || i >= m || j < 0 || j >= n || grid2[i][j] == 2 || grid2[i][j] == 0)
            return true;
        if (grid2[i][j] == 3)
            return false;

        if (grid1[i][j] == 1) {
            grid2[i][j] = 2;
            bool res = isSubIsland(grid1, grid2, i - 1, j) && isSubIsland(grid1, grid2, i + 1, j) && isSubIsland(grid1, grid2, i, j - 1) && isSubIsland(grid1, grid2, i, j + 1);
            if (!res)
                grid2[i][j] = 3;
            return res;
        }
        else 
            return false;
    }

    int countSubIslands(vector<vector<int>>& grid1, vector<vector<int>>& grid2) {
        int m = grid1.size();
        int n = grid1[0].size();

        int subIslands = 0;
        for (int i = 0; i < m; i++) 
            for (int j = 0; j < n; j++) 
                if (grid2[i][j] == 1) 
                    if (isSubIsland(grid1, grid2, i, j)) 
                        subIslands++;
        return subIslands;
    }
};
