class Solution {
public:

    int flag;

    void helper(vector<vector<int>>& g1, vector<vector<int>>& g2,int n,int m,int i,int j,vector<vector<bool>>&visit)
    {
        if(i>=n||i<0||j<0||j>=m||g2[i][j]==0||visit[i][j])
        {
            return;
        }
        else if(g1[i][j]==0)// if g2 cell contain land but g1 cell contain water it means that island is not the suitable    for subisland.
        {
            flag=false;
            return;
        }

        visit[i][j]=true;
        int dx[]={-1,0,1,0};
        int dy[]={0,1,0,-1};
        for(int t=0;t<4;t++)
        {
            int x=i+dx[t];
            int y=j+dy[t];
            helper(g1,g2,n,m,x,y,visit);
        }
    }



    int countSubIslands(vector<vector<int>>& g1, vector<vector<int>>& g2) {
        int n=g1.size(),m=g1[0].size();
